package com.wimoor.sys.gc.model.core;import java.io.Serializable;import com.wimoor.sys.gc.util.BeanDtoVoUtil;/** *  java 实体类entity/DTO/VO 相互转换，使用convert方法请确保 转换前对象不为空，和不能存在基础数据类型 * * @author wangsong * @mail 1720696548@qq.com * @date 2020/3/31 0031 15:26 * @return */public class Convert implements Serializable {    /**	 * 	 */	private static final long serialVersionUID = -5043593393097509922L;	/**     * @param clazz 自动转换后的JavaBean对象     * @param <T>     * @return     */    public <T> T convert(Class<T> clazz) {        return BeanDtoVoUtil.convert(this, clazz);    }}